<?php $_WJYKK_QUICKNAV = "";function staticText($db,$field,$replacements = null){	$sth = $db->prepare("select contents from staticmsg where field='$field'");	$sth->execute();	$result = $sth->fetch(PDO::FETCH_ASSOC);	if(count($result) == 0)	{		echo "WARNING! Invalid static field!";		return;	}		$contents = $result['contents'];	if($replacements != null && !isset($_SESSION['member']))	{		for($i = 0; $i < count($replacements); $i += 2)			$contents = str_replace($replacements[$i], $replacements[$i+1], $contents);	}		if(isset($_SESSION['member']))	{		echo "<form name='edit_$field' method='post' action='/res/static.php'><input type='hidden' name='ret' value='" . $_SERVER['REQUEST_URI'] . "'>";		echo "<input type='hidden' name='field' value='$field'><input type='hidden' name='contents' value=''><div id='static_$field' class='editable' contenteditable='true'>";	}	echo $contents;	if(isset($_SESSION['member']))	{		echo "</div><script type='text/javascript'>CKEDITOR.disableAutoInline = true;CKEDITOR.inline('static_$field');document.getElementById(\"static_$field\").addEventListener(\"input\",function(){";		echo "document.getElementById(\"href_$field\").style.backgroundColor=\"orange\"},false);</script>";		echo "(The above contents are editable for admins) &middot; ";		echo "(<a href='#' id='href_$field' onclick='javascript:document.edit_$field.contents.value=document.getElementById(\"static_$field\").innerHTML;document.edit_$field.submit();return false;'>Submit</a>)</form>";	}}function addQuickLink($title,$link,$type = "leaf"){	global $_WJYKK_QUICKNAV;	if($type == 'external') $type = "expanded";	elseif($type == 'anchor') $type = "collapsed";	$_WJYKK_QUICKNAV .= "<li class='$type'><a href='$link'>$title</a></li>";}function headon($title = null){	partialHead($title);	partialBody();}function partialHead($title = null){	if(session_status() !== PHP_SESSION_ACTIVE)		session_start();	echo "<!doctype html><html><head><title>";	if(@$t